import React from "react";
import { useFilters } from "../hooks/useFilters";
import PropertyCard from "../components/categories/PropertyCard";
import FilterSidebar from "../components/categories/FIlterSidebar";

export default function Categories() {
  const {
    filtered,
    totalCount,
    visibleCount,
    showMore,
    setFilters,
    resetFilters,
  } = useFilters();

  return (
    <main className="main-content">
      <FilterSidebar onFilterChange={setFilters} onReset={resetFilters} />
      <section className="property-listings">
        <h2>Available Properties</h2>
        <div className="results-count">
          Showing {Math.min(visibleCount, totalCount)} of {totalCount}{" "}
          properties
        </div>
        <div className="properties-grid">
          {filtered.map((p) => (
            <PropertyCard key={p.id} property={p} />
          ))}
        </div>
        {visibleCount < totalCount && (
          <button onClick={showMore}>Show More</button>
        )}
      </section>
    </main>
  );
}
