import React from "react";
import "./BenefitsSection.css";

interface Benefit {
  id: number;
  title: string;
  description: string;
  icon?: string; // Можна додати іконку, якщо потрібно
}

const benefits: Benefit[] = [
  {
    id: 1,
    title: "Висока якість",
    description: "Ми гарантуємо найкращу якість наших послуг та продуктів.",
    icon: "/icons/quality.svg",
  },
  {
    id: 2,
    title: "Швидка доставка",
    description: "Оперативна доставка у будь-який куточок країни.",
    icon: "/icons/delivery.svg",
  },
  {
    id: 3,
    title: "Професійна підтримка",
    description: "Завжди готові допомогти і відповісти на всі питання.",
    icon: "/icons/support.svg",
  },
];

const BenefitsSection: React.FC = () => {
  return (
    <section className="benefits-section">
      <h2>Наші переваги</h2>
      <div className="benefits-list">
        {benefits.map(({ id, title, description, icon }) => (
          <div key={id} className="benefit-card">
            {icon && (
              <img src={icon} alt={`${title} icon`} className="benefit-icon" />
            )}
            <h3>{title}</h3>
            <p>{description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default BenefitsSection;
