import React, { useState } from "react";
import PropertyCard from "../components/categories/PropertyCard";
import FilterSidebar from "../components/categories/FIlterSidebar";
import propertiesData from "../data/properties.json";

type FilterState = {
  transaction: "rent" | "sale";
  type: string;
  sort: string;
};

const Categories: React.FC = () => {
  const [filters, setFilters] = useState<FilterState>({
    transaction: "rent",
    type: "",
    sort: "newest",
  });

  // Фільтрація списку нерухомості на основі фільтрів
  const filteredProperties = propertiesData.filter((property) => {
    const matchTransaction = property.transaction === filters.transaction;
    const matchType =
      filters.type === "" ||
      property.type.toLowerCase().includes(filters.type.toLowerCase());

    return matchTransaction && matchType;
  });

  return (
    <div className="flex flex-col md:flex-row p-4 gap-4">
      <FilterSidebar filters={filters} onFilterChange={setFilters} />

      <div className="flex-1 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredProperties.map((property) => (
          <PropertyCard key={property.id} property={property} />
        ))}
      </div>
    </div>
  );
};

export default Categories;
