import { Property } from "../../types/Property";

const PropertyCard = ({ property }: { property: Property }) => {
  return (
    <div className="property-card border rounded p-4 shadow-md">
      <img
        src={property.image}
        alt={property.title}
        className="property-image w-full h-48 object-cover rounded"
        onError={(e) => {
          (e.target as HTMLImageElement).src = "/images/placeholder.jpg";
        }}
      />
      <div className="property-details mt-2">
        <h3 className="property-title font-semibold text-lg">
          {property.title}
        </h3>
        <div className="property-location text-sm text-gray-600">
          <i className="fas fa-map-marker-alt mr-1" /> {property.location}
        </div>
        <div className="property-features flex justify-between text-sm mt-1">
          <div className="feature">
            <i className="fas fa-bed mr-1" /> {property.bedrooms} room.
          </div>
          <div className="feature">
            <i className="fas fa-bath mr-1" /> {property.bathrooms} bath.
          </div>
          <div className="feature">
            <i className="fas fa-ruler-combined mr-1" /> {property.size} m²
          </div>
        </div>
        <div className="property-price text-xl font-bold mt-2">
          {property.transaction === "rent"
            ? `₴${property.price.toLocaleString("uk-UA")}/month`
            : `₴${property.price.toLocaleString("uk-UA")}`}
        </div>
        <div className="property-actions mt-3 flex justify-between">
          <button className="btn-details bg-blue-600 text-white px-3 py-1 rounded">
            More Info
          </button>
          <button className="btn-favorite text-red-500">
            <i className="far fa-heart" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default PropertyCard;
