// src/pages/Categories.tsx
import React, { useState, useEffect } from "react";
import Header from "../components/general/Header";
import Footer from "../components/general/Footer";
import PropertyCard from "../components/categories/PropertyCard";
import propertiesData from "../data/properties.json";
import Property from "../types/Property";

const Categories: React.FC = () => {
  const [filters, setFilters] = useState({
    transaction: "rent" as "rent" | "sale",
    type: "",
    location: "",
    minPrice: 0,
    maxPrice: Infinity,
    minSize: 0,
    maxSize: Infinity,
    amenities: [] as string[],
    sort: "newest",
  });

  const [visibleCount, setVisibleCount] = useState(10);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>([]);

  useEffect(() => {
    let result = propertiesData.filter((property) => {
      const matchesTransaction = property.transaction === filters.transaction;
      const matchesType = !filters.type || property.type === filters.type;
      const matchesLocation =
        !filters.location ||
        property.location
          .toLowerCase()
          .includes(filters.location.toLowerCase());
      const matchesPrice =
        property.price >= filters.minPrice &&
        property.price <= filters.maxPrice;
      const matchesSize =
        property.size >= filters.minSize && property.size <= filters.maxSize;
      const matchesAmenities = filters.amenities.every((amenity) =>
        property.amenities.includes(amenity)
      );

      return (
        matchesTransaction &&
        matchesType &&
        matchesLocation &&
        matchesPrice &&
        matchesSize &&
        matchesAmenities
      );
    });

    if (filters.sort === "price-asc") {
      result.sort((a, b) => a.price - b.price);
    } else if (filters.sort === "price-desc") {
      result.sort((a, b) => b.price - a.price);
    } else if (filters.sort === "newest") {
      result.sort(
        (a, b) =>
          new Date(b.dateAdded).getTime() - new Date(a.dateAdded).getTime()
      );
    } else if (filters.sort === "popular") {
      result.sort(() => 0); // Replace with actual popularity logic
    }
    //@ts-nocheck
    setFilteredProperties(result);
  }, [filters]);

  const handleAmenityChange = (amenity: string) => {
    setFilters((prev) => ({
      ...prev,
      amenities: prev.amenities.includes(amenity)
        ? prev.amenities.filter((a) => a !== amenity)
        : [...prev.amenities, amenity],
    }));
  };

  const resetFilters = () => {
    setFilters({
      transaction: "rent",
      type: "",
      location: "",
      minPrice: 0,
      maxPrice: Infinity,
      minSize: 0,
      maxSize: Infinity,
      amenities: [],
      sort: "newest",
    });
  };

  return (
    <>
      <Header />
      <main className="main-content">
        <aside className="filters-sidebar">
          <div className="filters-header">
            <h2>Filter Properties</h2>
            <button className="btn-reset" onClick={resetFilters}>
              Reset All
            </button>
          </div>

          {/* Transaction Type */}
          <div className="filter-group">
            <h3 className="filter-title">Rent or Sale</h3>
            <div className="filter-options">
              <label className="radio-option">
                <input
                  type="radio"
                  name="transaction-type"
                  value="rent"
                  checked={filters.transaction === "rent"}
                  onChange={() =>
                    setFilters((prev) => ({ ...prev, transaction: "rent" }))
                  }
                />
                <span className="radio-custom"></span>
                Rent
              </label>
              <label className="radio-option">
                <input
                  type="radio"
                  name="transaction-type"
                  value="sale"
                  checked={filters.transaction === "sale"}
                  onChange={() =>
                    setFilters((prev) => ({ ...prev, transaction: "sale" }))
                  }
                />
                <span className="radio-custom"></span>
                Sale
              </label>
            </div>
          </div>

          {/* Property Type */}
          <div className="filter-group">
            <h3 className="filter-title">Property Type</h3>
            <select
              className="filter-select"
              value={filters.type}
              onChange={(e) =>
                setFilters((prev) => ({ ...prev, type: e.target.value }))
              }
            >
              <option value="">All Types</option>
              <option value="apartment">Apartment</option>
              <option value="house">House</option>
              <option value="studio">Studio</option>
              <option value="villa">Villa</option>
            </select>
          </div>

          {/* Location */}
          <div className="filter-group">
            <h3 className="filter-title">Location</h3>
            <input
              type="text"
              className="filter-input"
              placeholder="City or region"
              value={filters.location}
              onChange={(e) =>
                setFilters((prev) => ({ ...prev, location: e.target.value }))
              }
            />
          </div>

          {/* Price */}
          <div className="filter-group" id="price-filter">
            <h3 className="filter-title">Price Range</h3>
            <div className="range-inputs">
              <input
                type="number"
                className="range-input"
                placeholder="Min"
                onChange={(e) =>
                  setFilters((prev) => ({
                    ...prev,
                    minPrice: Number(e.target.value) || 0,
                  }))
                }
              />
              <span className="range-separator">-</span>
              <input
                type="number"
                className="range-input"
                placeholder="Max"
                onChange={(e) =>
                  setFilters((prev) => ({
                    ...prev,
                    maxPrice: Number(e.target.value) || Infinity,
                  }))
                }
              />
            </div>
          </div>

          {/* Size */}
          <div className="filter-group">
            <h3 className="filter-title">Size (mÂ²)</h3>
            <div className="range-inputs">
              <input
                type="number"
                className="range-input"
                placeholder="Min"
                onChange={(e) =>
                  setFilters((prev) => ({
                    ...prev,
                    minSize: Number(e.target.value) || 0,
                  }))
                }
              />
              <span className="range-separator">-</span>
              <input
                type="number"
                className="range-input"
                placeholder="Max"
                onChange={(e) =>
                  setFilters((prev) => ({
                    ...prev,
                    maxSize: Number(e.target.value) || Infinity,
                  }))
                }
              />
            </div>
          </div>

          {/* Amenities */}
          <div className="filter-group">
            <h3 className="filter-title">Amenities</h3>
            <div className="checkbox-options">
              {["Balcony", "Garden", "Garage", "Pool", "Elevator"].map((a) => (
                <label className="checkbox-option" key={a}>
                  <input
                    type="checkbox"
                    checked={filters.amenities.includes(a)}
                    onChange={() => handleAmenityChange(a)}
                  />
                  <span className="checkbox-custom"></span>
                  {a}
                </label>
              ))}
            </div>
          </div>

          {/* Sort */}
          <div className="filter-group">
            <h3 className="filter-title">Sort By</h3>
            <select
              className="filter-select"
              value={filters.sort}
              onChange={(e) =>
                setFilters((prev) => ({ ...prev, sort: e.target.value }))
              }
            >
              <option value="newest">Newest First</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="popular">Most Popular</option>
            </select>
          </div>

          <button className="btn-apply-filters">Apply Filters</button>
        </aside>

        <section className="property-listings">
          <div className="listings-header">
            <h2>Available Properties</h2>
            <div className="results-count">
              Showing{" "}
              <span>{Math.min(visibleCount, filteredProperties.length)}</span>{" "}
              of <span>{filteredProperties.length}</span> properties
            </div>
          </div>

          <div className="properties-grid" id="properties-grid">
            {filteredProperties.slice(0, visibleCount).map((property) => (
              <PropertyCard key={property.id} property={property} />
            ))}
          </div>

          {visibleCount < filteredProperties.length && (
            <div className="load-more-container">
              <button
                className="btn-load-more"
                id="load-more-btn"
                onClick={() => setVisibleCount((prev) => prev + 10)}
              >
                Show More <i className="fas fa-chevron-down"></i>
              </button>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Categories;
