import React, { useEffect, useState } from "react";

type Property = {
  id: number;
  title: string;
  location: string;
  type: string;
  transaction: "rent" | "sale";
  price: number;
  size: number;
  bedrooms: number;
  bathrooms: number;
  amenities: string[];
  image: string;
  dateAdded: string;
};

const DEFAULTS = {
  minPrice: 0,
  maxPrice: 100000,
  minSize: 0,
  maxSize: 500,
};

export const PropertyFilter: React.FC = () => {
  const [properties, setProperties] = useState<Property[]>([]);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>([]);
  const [visibleCount, setVisibleCount] = useState(6);

  // Фільтри (всі контролі)
  const [transactionType, setTransactionType] = useState<string>("");
  const [propertyType, setPropertyType] = useState<string>("");
  const [locationSearch, setLocationSearch] = useState<string>("");
  const [minPrice, setMinPrice] = useState<number | "">(DEFAULTS.minPrice);
  const [maxPrice, setMaxPrice] = useState<number | "">(DEFAULTS.maxPrice);
  const [minSize, setMinSize] = useState<number | "">(DEFAULTS.minSize);
  const [maxSize, setMaxSize] = useState<number | "">(DEFAULTS.maxSize);
  const [amenities, setAmenities] = useState<string[]>([]);
  const [sortValue, setSortValue] = useState<string>("newest");

  // Завантажуємо дані з JSON (приклад із локального шляху)
  useEffect(() => {
    async function loadProperties() {
      try {
        const response = await fetch("../data/properties.json");
        if (!response.ok) throw new Error("Network response was not ok");
        const data: Property[] = await response.json();

        // Забезпечуємо, що amenities є масивом
        data.forEach((p) => {
          if (!p.amenities) p.amenities = [];
        });

        setProperties(data);
        setFilteredProperties(data);
      } catch (error) {
        console.error("Error loading properties:", error);
      }
    }
    loadProperties();
  }, []);

  // Функція фільтрації
  useEffect(() => {
    let filtered = properties.filter((property) => {
      const transactionMatch =
        !transactionType || property.transaction === transactionType;

      const searchTerm = locationSearch.trim().toLowerCase();
      const searchMatch =
        !searchTerm ||
        property.title.toLowerCase().includes(searchTerm) ||
        property.location.toLowerCase().includes(searchTerm);

      const typeMatch = !propertyType || property.type === propertyType;

      const priceMin = minPrice === "" ? DEFAULTS.minPrice : minPrice;
      const priceMax = maxPrice === "" ? DEFAULTS.maxPrice : maxPrice;
      const priceMatch =
        property.price >= priceMin && property.price <= priceMax;

      const sizeMin = minSize === "" ? DEFAULTS.minSize : minSize;
      const sizeMax = maxSize === "" ? DEFAULTS.maxSize : maxSize;
      const sizeMatch = property.size >= sizeMin && property.size <= sizeMax;

      const amenitiesMatch =
        amenities.length === 0 ||
        amenities.every((a) => property.amenities.includes(a));

      return (
        transactionMatch &&
        searchMatch &&
        typeMatch &&
        priceMatch &&
        sizeMatch &&
        amenitiesMatch
      );
    });

    // Сортування
    switch (sortValue) {
      case "price-asc":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-desc":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "newest":
        filtered.sort(
          (a, b) =>
            new Date(b.dateAdded).getTime() - new Date(a.dateAdded).getTime()
        );
        break;
      default:
        break;
    }

    setFilteredProperties(filtered);
    setVisibleCount(6); // Після фільтрації показуємо 6 спочатку
  }, [
    transactionType,
    propertyType,
    locationSearch,
    minPrice,
    maxPrice,
    minSize,
    maxSize,
    amenities,
    sortValue,
    properties,
  ]);

  // Обробник додавання/видалення з amenities
  const toggleAmenity = (amenity: string) => {
    setAmenities((prev) =>
      prev.includes(amenity)
        ? prev.filter((a) => a !== amenity)
        : [...prev, amenity]
    );
  };

  // Обробник Reset
  const resetFilters = () => {
    setTransactionType("");
    setPropertyType("");
    setLocationSearch("");
    setMinPrice(DEFAULTS.minPrice);
    setMaxPrice(DEFAULTS.maxPrice);
    setMinSize(DEFAULTS.minSize);
    setMaxSize(DEFAULTS.maxSize);
    setAmenities([]);
    setSortValue("newest");
  };

  // Показати більше
  const showMore = () => {
    setVisibleCount((prev) => prev + 6);
  };

  return (
    <main className="main-content">
      {/* Filters Sidebar */}
      <aside className="filters-sidebar">
        <div className="filters-header">
          <h2>Filter Properties</h2>
          <button type="button" className="btn-reset" onClick={resetFilters}>
            Reset All
          </button>
        </div>

        <div className="filter-group">
          <h3 className="filter-title">Rent or Sale</h3>
          <div className="filter-options">
            <label className="radio-option">
              <input
                type="radio"
                name="transaction-type"
                value="rent"
                checked={transactionType === "rent"}
                onChange={() => setTransactionType("rent")}
              />
              <span className="radio-custom"></span>
              Rent
            </label>
            <label className="radio-option">
              <input
                type="radio"
                name="transaction-type"
                value="sale"
                checked={transactionType === "sale"}
                onChange={() => setTransactionType("sale")}
              />
              <span className="radio-custom"></span>
              Sale
            </label>
          </div>
        </div>

        <div className="filter-group">
          <h3 className="filter-title">Property Type</h3>
          <select
            className="filter-select"
            id="sort-by"
            value={propertyType}
            onChange={(e) => setPropertyType(e.target.value)}
          >
            <option value="">All Types</option>
            <option value="apartment">Apartment</option>
            <option value="house">House</option>
            <option value="studio">Studio</option>
            <option value="villa">Villa</option>
          </select>
        </div>

        <div className="filter-group">
          <h3 className="filter-title">Location</h3>
          <input
            type="text"
            className="filter-input"
            placeholder="City or region"
            value={locationSearch}
            onChange={(e) => setLocationSearch(e.target.value)}
          />
        </div>

        <div className="filter-group" id="price-filter">
          <h3 className="filter-title">Price Range</h3>
          <div className="range-inputs">
            <input
              type="number"
              className="range-input"
              id="price-min"
              placeholder="Min"
              min={0}
              value={minPrice}
              onChange={(e) =>
                setMinPrice(
                  e.target.value === ""
                    ? ""
                    : Math.max(0, Number(e.target.value))
                )
              }
            />
            <span className="range-separator">-</span>
            <input
              type="number"
              className="range-input"
              id="price-max"
              placeholder="Max"
              min={0}
              value={maxPrice}
              onChange={(e) =>
                setMaxPrice(
                  e.target.value === ""
                    ? ""
                    : Math.max(0, Number(e.target.value))
                )
              }
            />
          </div>
        </div>

        <div className="filter-group" id="size-filter">
          <h3 className="filter-title">Size (m²)</h3>
          <div className="range-inputs">
            <input
              type="number"
              className="range-input"
              placeholder="Min"
              min={0}
              value={minSize}
              onChange={(e) =>
                setMinSize(
                  e.target.value === ""
                    ? ""
                    : Math.max(0, Number(e.target.value))
                )
              }
            />
            <span className="range-separator">-</span>
            <input
              type="number"
              className="range-input"
              placeholder="Max"
              min={0}
              value={maxSize}
              onChange={(e) =>
                setMaxSize(
                  e.target.value === ""
                    ? ""
                    : Math.max(0, Number(e.target.value))
                )
              }
            />
          </div>
        </div>

        <div className="filter-group">
          <h3 className="filter-title">Amenities</h3>
          <div className="checkbox-options">
            {["balcony", "garden", "garage", "pool", "elevator"].map(
              (amenity) => (
                <label key={amenity} className="checkbox-option">
                  <input
                    type="checkbox"
                    checked={amenities.includes(amenity)}
                    onChange={() => toggleAmenity(amenity)}
                  />
                  <span className="checkbox-custom"></span>
                  {amenity.charAt(0).toUpperCase() + amenity.slice(1)}
                </label>
              )
            )}
          </div>
        </div>

        <div className="filter-group">
          <h3 className="filter-title">Sort By</h3>
          <select
            className="filter-select"
            value={sortValue}
            onChange={(e) => setSortValue(e.target.value)}
          >
            <option value="newest">Newest First</option>
            <option value="price-asc">Price: Low to High</option>
            <option value="price-desc">Price: High to Low</option>
            <option value="popular">Most Popular</option>
          </select>
        </div>

        <button
          type="button"
          className="btn-apply-filters"
          onClick={() => {
            /* У нас автоматична фільтрація через useEffect, ця кнопка можна не використовувати,
               але для сумісності лишимо */
          }}
        >
          Apply Filters
        </button>
      </aside>

      {/* Property Listings */}
      <section className="property-listings">
        <div className="listings-header">
          <h2>Available Properties</h2>
          <div className="results-count">
            Showing{" "}
            <span>{Math.min(visibleCount, filteredProperties.length)}</span> of{" "}
            <span>{filteredProperties.length}</span> properties
          </div>
        </div>

        <div className="properties-grid" id="properties-grid">
          {filteredProperties.length === 0 ? (
            <div className="no-results">
              <p>Nothing found. Try changing your search parameters.</p>
            </div>
          ) : (
            filteredProperties.slice(0, visibleCount).map((property) => (
              <div key={property.id} className="property-card">
                <img
                  src={property.image}
                  alt={property.title}
                  className="property-image"
                  onError={(e) => {
                    (e.target as HTMLImageElement).onerror = null;
                    (e.target as HTMLImageElement).src =
                      "../images/placeholder.jpg";
                  }}
                />
                <div className="property-details">
                  <h3 className="property-title">{property.title}</h3>
                  <div className="property-location">
                    <i className="fas fa-map-marker-alt"></i>{" "}
                    {property.location}
                  </div>
                  <div className="property-features">
                    <div className="feature">
                      <i className="fas fa-bed"></i> {property.bedrooms} room.
                    </div>
                    <div className="feature">
                      <i className="fas fa-bath"></i> {property.bathrooms} bath.
                    </div>
                    <div className="feature">
                      <i className="fas fa-expand"></i> {property.size} m²
                    </div>
                  </div>
                  <div className="property-price">
                    ${property.price.toLocaleString()}
                  </div>
                  <button className="btn-details">View Details</button>
                </div>
              </div>
            ))
          )}
        </div>

        {visibleCount < filteredProperties.length && (
          <button className="btn-show-more" onClick={showMore}>
            Show More
          </button>
        )}
      </section>
    </main>
  );
};
