import React from "react";
import { Filters } from "../../types/Property";

interface FilterSidebarProps {
  filters: Filters;
  onFilterChange: React.Dispatch<React.SetStateAction<Filters>>;
}

const FilterSidebar: React.FC<FilterSidebarProps> = ({
  filters,
  onFilterChange,
}) => {
  return (
    <aside className="w-full md:w-1/4 p-4 bg-gray-100 rounded-lg">
      <h2 className="text-lg font-semibold mb-4">Filters</h2>

      <div className="mb-4">
        <label className="block font-medium mb-1">Transaction</label>
        <select
          value={filters.transaction}
          onChange={(e) =>
            onFilterChange((prev) => ({
              ...prev,
              transaction: e.target.value as "rent" | "sale" | "",
            }))
          }
          className="w-full p-2 border rounded"
        >
          <option value="">Any</option>
          <option value="rent">Rent</option>
          <option value="sale">Sale</option>
        </select>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Type</label>
        <input
          type="text"
          value={filters.propertyType}
          onChange={(e) =>
            onFilterChange((prev) => ({
              ...prev,
              propertyType: e.target.value,
            }))
          }
          className="w-full p-2 border rounded"
          placeholder="e.g. Apartment"
        />
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Search</label>
        <input
          type="text"
          value={filters.searchTerm}
          onChange={(e) =>
            onFilterChange((prev) => ({ ...prev, searchTerm: e.target.value }))
          }
          className="w-full p-2 border rounded"
          placeholder="Search by title or location"
        />
      </div>

      {/* Тут можна додати фільтр по аменітіз, площі, ціні якщо потрібно */}

      <div>
        <label className="block font-medium mb-1">Sort By</label>
        <select
          value={filters.sortValue}
          onChange={(e) =>
            onFilterChange((prev) => ({
              ...prev,
              sortValue: e.target.value as Filters["sortValue"],
            }))
          }
          className="w-full p-2 border rounded"
        >
          <option value="newest">Newest</option>
          <option value="priceLowHigh">Price: Low to High</option>
          <option value="priceHighLow">Price: High to Low</option>
          <option value="popular">Most Popular</option>
        </select>
      </div>
    </aside>
  );
};

export default FilterSidebar;
