import React, { useEffect, useState } from "react";
import PropertyCard from "../components/categories/PropertyCard";
import { Property, Filters } from "../types/Property";

const DEFAULTS = {
  minPrice: 0,
  maxPrice: 100000,
  minSize: 0,
  maxSize: 500,
};

export const PropertyPage: React.FC = () => {
  const [properties, setProperties] = useState<Property[]>([]);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>([]);
  const [visibleCount, setVisibleCount] = useState(6);
  const [filters, setFilters] = useState<Filters>({
    transactionType: "",
    propertyType: "",
    searchTerm: "",
    minPrice: DEFAULTS.minPrice,
    maxPrice: DEFAULTS.maxPrice,
    minSize: DEFAULTS.minSize,
    maxSize: DEFAULTS.maxSize,
    selectedAmenities: [],
    sortValue: "newest",
  });

  useEffect(() => {
    fetch("../data/properties.json")
      .then((res) => res.json())
      .then((data: Property[]) => {
        const updated = data.map((p) => ({
          ...p,
          amenities: p.amenities || [],
        }));
        setProperties(updated);
        setFilteredProperties(updated);
      })
      .catch(() => {
        setFilteredProperties([]);
      });
  }, []);

  useEffect(() => {
    filterProperties();
  }, [filters, properties]);

  const filterProperties = () => {
    let result = properties.filter((property) => {
      const transactionMatch =
        !filters.transactionType ||
        property.transaction === filters.transactionType;

      const matchesSearch =
        !filters.searchTerm ||
        property.title
          .toLowerCase()
          .includes(filters.searchTerm.toLowerCase()) ||
        property.location
          .toLowerCase()
          .includes(filters.searchTerm.toLowerCase());

      const matchesType =
        !filters.propertyType || property.type === filters.propertyType;

      const matchesPrice =
        property.price >= filters.minPrice &&
        property.price <= filters.maxPrice;

      const matchesSize =
        property.size >= filters.minSize && property.size <= filters.maxSize;

      const matchesAmenities =
        filters.selectedAmenities.length === 0 ||
        filters.selectedAmenities.every((a) => property.amenities.includes(a));

      return (
        transactionMatch &&
        matchesSearch &&
        matchesType &&
        matchesPrice &&
        matchesSize &&
        matchesAmenities
      );
    });

    switch (filters.sortValue) {
      case "price-asc":
        result.sort((a, b) => a.price - b.price);
        break;
      case "price-desc":
        result.sort((a, b) => b.price - a.price);
        break;
      case "newest":
        result.sort(
          (a, b) =>
            new Date(b.dateAdded).getTime() - new Date(a.dateAdded).getTime()
        );
        break;
    }

    setFilteredProperties(result);
    setVisibleCount(6); // Reset on filter change
  };

  const handleLoadMore = () => {
    setVisibleCount((prev) => prev + 6);
  };

  return (
    <main className="main-content">
      {/* Тут буде компонент фільтрів */}
      {/* Тут буде компонент зі списком карток нерухомості */}
    </main>
  );
};
