import React from "react";

interface FilterSidebarProps {
  filters: {
    transaction: "rent" | "sale";
    type: string;
    sort: string;
  };
  onFilterChange: React.Dispatch<
    React.SetStateAction<FilterSidebarProps["filters"]>
  >;
}

const FilterSidebar: React.FC<FilterSidebarProps> = ({
  filters,
  onFilterChange,
}) => {
  return (
    <aside className="w-full md:w-1/4 p-4 bg-gray-100 rounded-lg">
      <h2 className="text-lg font-semibold mb-4">Filters</h2>

      <div className="mb-4">
        <label className="block font-medium mb-1">Transaction</label>
        <select
          value={filters.transaction}
          onChange={(e) =>
            onFilterChange((prev) => ({
              ...prev,
              transaction: e.target.value as "rent" | "sale",
            }))
          }
          className="w-full p-2 border rounded"
        >
          <option value="rent">Rent</option>
          <option value="sale">Sale</option>
        </select>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Type</label>
        <input
          type="text"
          value={filters.type}
          onChange={(e) =>
            onFilterChange((prev) => ({ ...prev, type: e.target.value }))
          }
          className="w-full p-2 border rounded"
          placeholder="e.g. Apartment"
        />
      </div>

      <div>
        <label className="block font-medium mb-1">Sort By</label>
        <select
          value={filters.sort}
          onChange={(e) =>
            onFilterChange((prev) => ({ ...prev, sort: e.target.value }))
          }
          className="w-full p-2 border rounded"
        >
          <option value="newest">Newest</option>
          <option value="lowToHigh">Price: Low to High</option>
          <option value="highToLow">Price: High to Low</option>
          <option value="popular">Most Popular</option>
        </select>
      </div>
    </aside>
  );
};

export default FilterSidebar;
