import React, { useEffect, useState } from "react";
import PropertyCard from "../components/categories/PropertyCard";
import data from "../data/properties.json";
import { Property, Filters } from "../types/Property";

const DEFAULTS = {
  minPrice: 0,
  maxPrice: 100000,
  minSize: 0,
  maxSize: 500,
};

export const Categories: React.FC = () => {
  const [properties, setProperties] = useState<Property[]>([]);
  const [filteredProperties, setFilteredProperties] = useState<Property[]>([]);
  const [visibleCount, setVisibleCount] = useState(6);
  const [filters, setFilters] = useState<Filters>({
    transaction: "",
    propertyType: "",
    searchTerm: "",
    minPrice: DEFAULTS.minPrice,
    maxPrice: DEFAULTS.maxPrice,
    minSize: DEFAULTS.minSize,
    maxSize: DEFAULTS.maxSize,
    selectedAmenities: [],
    sortValue: "newest",
  });

  useEffect(() => {
    // Імпортуємо data напряму, додаємо amenities, якщо нема
    const updated = (data as Property[]).map((p) => ({
      ...p,
      amenities: p.amenities || [],
    }));
    setProperties(updated);
    setFilteredProperties(updated);
  }, []);

  useEffect(() => {
    filterProperties();
  }, [filters, properties]);

  const filterProperties = () => {
    let result = properties.filter((property) => {
      const transactionMatch =
        !filters.transaction || property.transaction === filters.transaction;

      const matchesSearch =
        !filters.searchTerm ||
        property.title
          .toLowerCase()
          .includes(filters.searchTerm.toLowerCase()) ||
        property.location
          .toLowerCase()
          .includes(filters.searchTerm.toLowerCase());

      const matchesType =
        !filters.propertyType || property.type === filters.propertyType;

      const matchesPrice =
        property.price >= filters.minPrice &&
        property.price <= filters.maxPrice;

      const matchesSize =
        property.size >= filters.minSize && property.size <= filters.maxSize;

      const matchesAmenities =
        filters.selectedAmenities.length === 0 ||
        filters.selectedAmenities.every((a) => property.amenities.includes(a));

      return (
        transactionMatch &&
        matchesSearch &&
        matchesType &&
        matchesPrice &&
        matchesSize &&
        matchesAmenities
      );
    });

    switch (filters.sortValue) {
      case "priceLowHigh":
        result.sort((a, b) => a.price - b.price);
        break;
      case "priceHighLow":
        result.sort((a, b) => b.price - a.price);
        break;
      case "newest":
      default:
        result.sort(
          (a, b) =>
            new Date(b.dateAdded).getTime() - new Date(a.dateAdded).getTime()
        );
        break;
    }

    setFilteredProperties(result);
    setVisibleCount(6);
  };

  const handleLoadMore = () => {
    setVisibleCount((prev) => prev + 6);
  };

  return (
    <main className="main-content">
      <div className="properties-list">
        {filteredProperties.slice(0, visibleCount).map((property) => (
          <PropertyCard key={property.id} property={property} />
        ))}
      </div>
      {visibleCount < filteredProperties.length && (
        <div className="load-more-wrapper">
          <button className="load-more-btn" onClick={handleLoadMore}>
            Load More
          </button>
        </div>
      )}
    </main>
  );
};
