import React from "react";
import PropertyCard from "../components/categories/PropertyCard";
import FilterSidebar from "../components/categories/FIlterSidebar";
import { useFilters } from "../hooks/useFilters";

export const Categories: React.FC = () => {
  const { filtered, totalCount, visibleCount, showMore, filters, setFilters } =
    useFilters();

  return (
    <main className="main-content flex flex-col md:flex-row gap-4 p-4">
      <FilterSidebar filters={filters} onFilterChange={setFilters} />
      <section className="properties-list flex-1 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filtered.map((property) => (
          <PropertyCard key={property.id} property={property} />
        ))}
      </section>

      {visibleCount < totalCount && (
        <div className="load-more-wrapper col-span-full text-center mt-4">
          <button
            className="load-more-btn bg-blue-600 text-white px-4 py-2 rounded"
            onClick={showMore}
          >
            Load More
          </button>
        </div>
      )}
    </main>
  );
};
